import React, { useState } from "react";

import { AppointmentForm } from "../../components/appointmentForm/AppointmentForm";
import { TileList } from "../../components/tileList/TileList";

export const AppointmentsPage = ({appointments, addNewAppointment}) => {
  
  const [title, setTitle] = useState('');
  const [contact, setContact] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if(title !== '' || contact !== '' || date !== '' || time !== '') {
    const newAppointment = {
        title: title,
        contact: contact,
        date: date,
        time: time
      }
      setAppointments(newAppointment);
    }
  };

  return (
    <div>
      <section>
        <h2>Add Appointment</h2>
        <AppointmentForm handleSubmit={handleSubmit} setTitle={setTitle} setContact={setContact} setDate={setDate} setTime={setTime} />
      </section>
      <hr />
      <section>
        <h2>Appointments</h2>
        {/* <TileList appointments={appointments} /> */}
      </section>
    </div>
  );
};
