import React from "react";

export const ContactPicker = ({contacts, setContact, name, phone, email}) => {

  const options = 
    contacts.map(contact => (
      <option value={contact.name}>{contact.name}</option>
    ))
  

  return (
    <select 
        name="name" 
        options = {
          contacts.map((contact) => (
            <option value={contact.name}>{contact.name}</option>
          ))
          }
        onChange={(e) => setContact(e.target.value)}
        />
  );
};
