import React from "react";

export const ContactPicker = ({contacts, setContact, name, phone, email}) => {

  
  return (
    <select 
        name="name" 
        defaultValue={ {value: '', label: 'No Contact Selected'}}
        options = {
          contacts.map((contact) => (
            <option value={contact.value}>{contact.name}</option>
          ))
          }
        onChange={(e) => setContact(e.target.value)}
        />
  );
};
